import { useState } from 'react';
import axios from 'axios';

const LoginForm = () => {

    const [username, setUsername] = useState('');

    const [password, setPassword] = useState('');

    const handleSubmit = (event) =>
    {
            event.preventDefault();
            const authObj = {'Project-ID':"075271a9-4ef7-455c-8dfe-765e6af39bf5",'User-Name':username,'User-Secret':password}

            try{
                axios.get('https://api.chatengine.io/chats',{headers:authObj});
                localStorage.setItem('username',username);
                localStorage.setItem('password',password);
                window.location.reload();
            }
            catch(error){

            }
        

             
    }

    return (
        <div className="wrapper">
            <div className="form">
                <h1 className="title">Chat Application</h1>
                <form onSubmit={handleSubmit}>

                    <input
                        type = "text"
                        value = {username}
                        className="input"
                        placeholder = "Type Username"
                        required
                        onChange={
                            (e)=>setUsername(e.target.value)
                        }
                    /><input
                        type = "password"
                        value = {password}
                        className="input"
                        placeholder = "Type Password"
                        required
                        onChange={
                            (e)=>setPassword(e.target.value)
                        }
                    />
                    <div align="center"></div>
                    <button type="submit" className="button" style={{marginLeft:"50px",height:"60px",width:"100px"}}>
                    <span>
                        Start
                    </span>

                    </button>
                </form>
            </div>
        </div>

    )


}

export default LoginForm;
